    forwarding: function(config) {
        if(this.get('disposisi_masuk_isterus') == 1) return;
                config = Ext.apply({
                    callback: Ext.emptyFn,
                    success: Ext.emptyFn,
                    failure: Ext.emptyFn,
                    scope: this
                }, config);

                this.set({
                    'disposisi_masuk_terus_staf': config.staf,
                    'disposisi_masuk_terus_tgl' : new Date(),
                    'disposisi_tgl' : new Date()
                });
                this.save(config);
    },

    reading: function(config) {
        if(this.get('disposisi_masuk_isbaca') == this.self.statusBaca().BACA_BACA) return;
        if(this.get('disposisi_masuk_isterus') == this.self.statusTeruskan().TERUSKAN_TERUSKAN) return;
        config = Ext.apply({
            callback: Ext.emptyFn,
            success: Ext.emptyFn,
            failure: Ext.emptyFn,
            scope: this
        }, config);

        this.set({
            'disposisi_masuk_baca_staf' : config.staf,
            'disposisi_masuk_baca_tgl': new Date()
        });
        this.save(config);
    },

    isTerimaBerkas: function(config) {
        config = Ext.apply({
            callback: Ext.emptyFn,
            success: Ext.emptyFn,
            failure: Ext.emptyFn,
            scope: this
        }, config);

        this.set({
            'disposisi_masuk_berkasterima_tgl' : new Date(),
            'disposisi_masuk_berkasterima_staf' : config.staf
        });
        this.save(config);
    },

