Ext.define('SIPAS.view.Sipas.com.button.Toogle', {
    extend: 'Ext.button.Button',
    alias: 'widget.sipas_com_button_toogle',

    textTpl: '<i class="{iconCls}"></i>',
    toogled: false,
    toogledIconCls: 'tango-32 go-first',
    toogleIconCls: 'tango-32 go-last',
    scale: 'large',
    text: '',

    initComponent: function() {
        var me = this;

        me.addEvents(
            'toogle'
        );

        Ext.applyIf(me, {
            listeners: {
                afterrender: {
                    fn: me.onButtonToogleAfterRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onButtonToogleAfterRender: function(component, eOpts) {
        component.initIcon();
    },

    toogle: function() {
        var button = this;
        button.toogled = !button.toogled;
        button.initIcon();
        button.fireEvent('toogle',button, button.toogled);
    },

    initIcon: function() {
        var button = this;
        var iconCls = button.toogled ? button.toogledIconCls : button.toogleIconCls;
        button.setText((new Ext.Template(button.textTpl)).apply({
            iconCls: iconCls
        }));
    }

});