    onRetensiAfterRender: function(component, eOpts) {
        var c = component.up('#containerJenis').down('[name=jenis_retensi]') || {};
        if(c.readOnly) {
            component.hide();
        }else{
            component.getStore().reload();
            component.setValue(0);

        }
    },

    onRetensiSelect: function(combo, records, eOpts) {
        numberfield = combo.up('#containerJenis').down('[name=jenis_retensi]');

        if(!numberfield.readOnly){
            numberfield.setValue(combo.getValue());
        }
    }

