    onPegawai_nipKeyup: function(textfield, e, eOpts) {
        this.username = this.username || textfield.up('form').down('#textfieldUsername');
        if(this.username){
            var val = Ext.String.trim(textfield.getValue()).replace(/\s/g,'').replace(/_{2,}/g,'_');
            this.username.setValue(val);
        }
    },

    onCheckboxfieldChange: function(field, newValue, oldValue, eOpts) {
        var password = field.next('textfield');
        password.allowBlank = !newValue;
        password.setReadOnly(!newValue);
        if(!newValue) password.setValue();

        password.emptyText = (newValue ? field.checkText : field.uncheckText);
        password.reset();
    },

    onCheckboxfieldAfterRender: function(component, eOpts) {
        component.fireEvent('change',component,component.getValue(), !component.getValue())
    }

