        margin-bottom: 0;
        font-weight: normal;
        left: 0;
        right: 0;
        top: auto;
        bottom: 0;
        /*position: absolute;*/
        padding: 40px 0px 10px 0px;
    }

    table.content-body {
        width: 100%;
        font-family: Arial;
        color: #333333;
        margin-top: 30px;
        border-collapse: collapse;
    }

    table.content-body thead {
        display: table;
        width: 100%;
        height: 50px;
    }

    table.content-body thead tr {
        font-size: 16px;
        padding-top: 10px;
    }

    table.content-body thead tr th {
        font-weight: normal;
    }

    table.content-body tbody {
        display: block;
        overflow: auto;
        float: left;
        width: 100%;
    }

    table.content-body tbody tr {
        display: table;
        width: 100%;
    }

    table.content-body tbody tr.ext-disabled {
        display: table;
        width: 100%;
        cursor: not-allowed;
        pointer-events: none;
    }

    table.content-body th, td {
        width: 33%;
        padding: 8px;
        padding: 16px 16px 16px 16px;
    }

    table.content-body th {
        background-color: #EEEEEE;
        color: #616161;
        font-size: 14px;
    }

    table.content-body tr {
        border-bottom: 1px solid #e0e0e0;
    }

    table.content-body tr.ext-disabled {
        cursor: not-allowed;
        pointer-events: none;
    }

    table.content-body tr:hover td {
        color: #1976D2;
        cursor: pointer;
    }

    table.content-body tr.ext-disabled:hover td {
        cursor: not-allowed;
        pointer-events: none;
    }

    table.content-body tr:hover td a span {
        color: #1976D2;
    }

    table.content-body tr td a.ext-disabled:hover span{
        color: #9E9E9E;
        cursor: not-allowed;
    }

    table.content-body tr:hover .mui-button {
        /*  background:#EEEEEE;*/
        opacity: 1;
    }

    table.content-body tr td input:hover {
        background: #ffffff;
        color: #1976D2;
        border: 1px solid #1976D2;
        
    }

    /*  table.content tr:nth-child(odd) {
