    }

    .unit-name {
        color: #3d464d;
    }

    .line {
        padding-top: 20px;
        border-bottom: groove;
    }

    .name {
        display: block;
        color: #000000;
    }

    .mui-button {
        opacity: 0;
        min-width: 88px;
        line-height: 32px;
        color: #616161;
        background-color: #ffffff;
        border: 1px solid #C1C7CD;
        cursor: pointer;
        font-size: 14px;
        border-radius: 3px;
        /*  box-shadow: 0 3px 3px 0 #666;*/
        padding: 0px 16px 0px 16px;
        text-align: center;
        text-decoration: none;
    }

    .mui-button:active{
      position: relative;
      overflow: hidden;
      color: #ffffff;
      background-color: #1976D2;
    }

    .mui-button:focus{
      outline: none;
    }

    .ext-disabled {
        pointer-events: none;
        cursor: not-allowed;
        text-decoration: none;
    }
    .button-hidden {
        visibility: hidden;
    }

