<body>
    <div id="header">
        <span class="title">Arsip Bebas</span>
    </div>
    <div id="content">
        <div class="content-header">
            <span class="title">{perihal}</span>
            <span class="unit-name">{pembuat_unit}</span>
        </div>

        <!-- <div class="list-group">
            <ul>
                <li class="header">Berkas</li>
                <li class="list">
                    <i class="icon ext-docx"></i>
                    <a class="file-info">
                        <span class="file-name">File</span>
                        <span class="file-detail">31 Desember 2017 00:00 - 100 kb</span>
                    </a>
                    <a class="action">
                        <input class="mui-button" type="button" value="Download"/>
                    </a>
                </li>
            </ul>
        </div> -->

        <table class="content-body">
            <thead>
                <tr>
                    <th align="left" style="width:50%; text-align:left;">Berkas</th>
                </tr>
            </thead>
            <tbody>
            {dokumen}
                <tr class="{dokumen_disabled}">
                    <td style="width:1%; text-align:center;">
                        <i class="{dokumen_icon}"></i>
                    </td>
                    <td style="text-align:left;">
                        <a style="text-decoration:none;" {dokumen_link} class="{dokumen_disabled} filename">
                          <span class="name">{dokumen_nama}{dokumen_ext}</span>
                          <span class="info">{properti_buat_tgl} - {dokumen_size} kb</span> 
                        </a>
                    </td>
                    <td align="right">
                        <button class="mui-button {dokumen_button_hidden} ripple" type="button" onclick="location.href='{dokumen_base_url}server.php/sipas/dokumen/download/{dokumen_id}'">Download</button>
                    </td>
                </tr>
            {/dokumen}
            </tbody>
        </table>
        <div class="content-footer">
            <span class="arch-footer-left">* Dokumen dengan ekstensi .sdoc dan .link tidak bisa di download.</span>
            <span class="arch-footer-right">{jumlah_dokumen} berkas.</span>
        </div>
    </div>
    <div id="footer">
        <span>{footer}</span>
    </div>
</body>

